import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import "../Style/Busqueda.css"
import "../Style/TypeColor.css"
const Busqueda = () => {
    const [pokemon, setPokemon] = useState ("pikachu");
    const [informacion, setinformacion] = useState ([]);

    async function datos() {
        try {
            const resultados = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
            console.log(resultados.data);
            setinformacion(resultados.data);
            return resultados.data;
        } catch (error) {
            console.error('Error fetching data for', pokemon, error);
        }
    }
    useEffect(()=>{
        datos()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[pokemon]);

    const handlePokemonChange = (e) => {
        const value = e.target.value;
        if (value !== "0") {
            setPokemon(value);
    }}

    return ( 
        <main id="Busqueda">  
            <h1>Searching Pokemon Tool</h1>
            <h2>Wirte down the Pokemon's numbre or name to search it's information</h2>
            <input type="text" placeholder={pokemon} onChange={() => setTimeout(handlePokemonChange, 20000)}/>
            {/* <div className={informacion.types[0].type.name}> */}
                <p>{informacion.name}</p>
            {/* </div> */}

        </main>
    );
}

export default Busqueda;
