import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import "../Style/Busqueda.css"
import "../Style/TypeColor.css"
import pokeball from "../Images/pokeball1.png"

const Busqueda = () => {
    const [pokemon, setPokemon] = useState ("pikachu");
    const [informacion, setinformacion] = useState ([]);
    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    async function datos() {
        try {
            const resultados = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
            console.log(resultados.data);
            setinformacion(resultados.data);
            return resultados.data;
        } catch (error) {
            console.error('Error fetching data for', pokemon, error);
        }
    }
    useEffect(()=>{
        datos()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[pokemon]);
    return ( 
        <main id="Busqueda">  
            <h1>Searching Pokemon Tool</h1>
            <h2>Wirte down the Pokemon's numbre or name to search it's information</h2>
            <div>
                <input type="text" placeholder={pokemon} value={inputValue} onChange={handleInputChange} />
                <button onClick={() => setPokemon(inputValue)}>Search</button>
            </div>
            <div>

            </div>
                <p>{informacion.name}</p>
                <img src={informacion.sprites.other['official-artwork'].front_default} alt={pokeball} />

        </main>
    );
}

export default Busqueda;
