import React, { useState, useEffect, useRef } from "react";
import '../Style/AreaDetalles.css'

const AreaDetalles = (props) => {
    const [Normal, setNormal] = useState(true);
    const statsbarRefs = useRef([]);


    const hadleClickNormal = () => {setNormal(true);};
    const hadleClickShiny = () => {setNormal(false);};

    useEffect(() => {
        statsbarRefs.current.forEach((statsbar) => {
            statsbar.classList.remove("StatsbarAnimation");
            void statsbar.offsetWidth;
            statsbar.classList.add("StatsbarAnimation");
        });
    }, [props]);

    const addStatsbarRef = (ref) => {
        if (ref && !statsbarRefs.current.includes(ref)) {
            statsbarRefs.current.push(ref);
        }
    };

    return (
        <div>
            <div className={`${props.types}detalles`}>
            <div><button id="CloseX" onClick={() => props.clearData()}>X</button></div>
                <div id="AreaSuperior">
                    <div id="cabecera">
                        <p>#&nbsp;&nbsp;{props.id}.</p>
                        <p>{props.name}</p>
                    </div>
                    <div id="imagenes">
                        <div  >
                            <button className="SpriteSelector" onClick={hadleClickNormal}>Normal</button>
                            <button className="SpriteSelector" onClick={hadleClickShiny}>Shiny</button>
                        </div>
                        {Normal 
                            ? (<img id='Normal' src={props.sprite} className="Sprites" alt="" srcset="" />)
                            : (<img id='Shiny' src={props.shinySprite} className="Sprites" alt="" srcset="" />)
                        }
                    </div>
                    <div id="tipo">
                        <p className={props.types}>{props.types}</p>
                        <p className={props.type2}>{props.type2}</p>
                    </div>
                </div>
                <div id="AreaIntermedia">
                    <div id="Col1">
                        <span>Ability:</span>
                        <span>Hidden Ability:</span>
                        <span>Height:</span>
                        <span>Weight:</span>
                    </div>
                    <div id="Col2">
                        <span>{props.ability}</span>
                        <span>{props.hiddenAbility}</span>
                        <span>{props.height}</span>
                        <span>{props.weight}</span>
                    </div>
                </div>
                <div id="Stats">
                    <h3>Pokemon's Stats</h3>
                    <div className="StatsDiv">
                        <span>HP: {props.hp}/255</span>
                        <div className="Statsbar StatsbarAnimation" 
                        style={{ "--percent": props.hp}}
                        ref={addStatsbarRef}></div>
                    </div>
                    <div className="StatsDiv">
                        <span>ATK: {props.atk}/255</span>
                        <div className="Statsbar" 
                        style={{ "--percent": props.atk}}
                        ref={addStatsbarRef}></div>
                    </div>
                    <div className="StatsDiv">
                        <span>Def: {props.def}/255</span>
                        <div className="Statsbar" 
                        style={{ "--percent": props.def}}
                        ref={addStatsbarRef}></div>
                    </div>
                    <div className="StatsDiv">
                        <span>SPATK:{props.spAtk}/255</span>
                        <div className="Statsbar" 
                        style={{ "--percent": props.spAtk}}
                        ref={addStatsbarRef}></div>
                    </div>
                    <div className="StatsDiv">
                        <span>SPDEF: {props.spDef}/255</span>
                        <div className="Statsbar" 
                        style={{ "--percent": props.spDef}}
                        ref={addStatsbarRef}></div>
                    </div>
                    <div className="StatsDiv">
                        <span>Speed: {props.speed}/255</span>
                        <div className="Statsbar" 
                        style={{ "--percent": props.speed}}
                        ref={addStatsbarRef}></div>
                    </div>
                </div>
                <button id="Close" onClick={() => props.clearData()}>Close</button>
            </div>
        </div>
    );
}

export default AreaDetalles;
