import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import "../Style/Busqueda.css"
const Busqueda = () => {
    const [pokemon, setPokemon] = useState ("pikachu");
    const [info, setinfo] = useState ([]);

    async function datos() {
        try {
            const resultados = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
            setinfo(resultados.data);
            return resultados.data;
        } catch (error) {
            console.error('Error fetching data for', pokemon, error);
        }
    }
    useEffect(()=>{
        datos()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[pokemon]);
    return ( 
        <main id="Busqueda">  
            <h1>Searching Pokemon Tool</h1>
            <h2>Wirte down the Pokemon's numbre or name to search it's information</h2>
            <input type="text" placeholder={pokemon}/>
            <span>{`# ${info.id}`}</span>
            <span>{info.name}</span>
            <span>{`${info.height / 10} meters`}</span>
            <span>{`${info.weight / 10} kilograms`}</span>
            <span>{info.types[0].type.name}</span>
            <span>{info.types.length > 1 ? info.types[1].type.name : ""}</span>
            <span>{info.abilities.map(ability => ability.ability.name)}</span>
            {/* <span>{info.abilities.map(ability => ability.is_hidden)}</span> */}
        </main>
    );
} 

export default Busqueda;
