import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import "../Style/Busqueda.css"
import pokeball from "../Images/pokeball1.png"

const Busqueda = () => {
    const [pokemon, setPokemon] = useState ("");
    const [info, setinfo] = useState ([]);


    const [pokeId, setPokeId] = useState("");
	const [pokeName, setPokeName] = useState("");
	const [pokeType, setPokeType] = useState("");
	const [pokeType2, setPokeType2] = useState("");
	const [pokeAbility, setPokeAbility] = useState("");
	const [pokeHiddenAbility, setPokeHiddenAbility] = useState("");
	const [pokeHeight, setPokeHeight] = useState("");
	const [pokeWeight, setPokeWeight] = useState("");
	const [pokeSprite, setPokeSprite] = useState("");
	const [pokeShinySprite, setPokeShinySprite] = useState("");
	const [pokeHp, setPokeHp] = useState("");
	const [pokeAtk, setPokeAtk] = useState("");
	const [pokeDef, setPokeDef] = useState("");
	const [pokeSpAtk, setPokeSpAtk] = useState("");
	const [pokeSpDef, setPokeSpDef] = useState("");
	const [pokeSpeed, setPokeSpeed] = useState("");
    const [pokeMoves, setPokeMoves] = useState("");
    async function datos() {
        try {
            const resultados = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
            setinfo(resultados.data);
            console.log(resultados.data);
            return resultados.data;
        } catch (error) {
            console.error('Error fetching data for', pokemon, error);
        }
    }
    useEffect(()=>{
        datos()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[pokemon]);
    return ( 
        <main id="Busqueda">  
            <h1>Searching Pokemon Tool</h1>
            <h2>Wirte down the Pokemon's numbre or name to search it's information</h2>
            <input type="text" placeholder={pokemon} 
                onChange={(e) => {
                    setPokemon(e.target.value);
                    setPokeSprite(info.sprites.other['official-artwork'].front_default 
						? info.sprites.other['official-artwork'].front_default 
						: pokeball);
					setPokeShinySprite(info.sprites.other['official-artwork'].front_shiny 
						? info.sprites.other['official-artwork'].front_shiny 
						: pokeball);
                        setPokeId(info.id);
                        setPokeName(info.name);
                        setPokeType(info.types[0].type.name);
                        setPokeType2(info.types.length > 1 
                            ? info.types[1].type.name 
                            : null);
                        setPokeHeight(info.height);
                        setPokeWeight(info.weight ? `${info.weight}` : "0");
                        // ABILITIES
                        const ability = info.abilities.map(ability => ability.ability.name);
                        const isHidden = info.abilities.map(ability => ability.is_hidden);
                        setPokeAbility(ability);
                        setPokeHiddenAbility(isHidden);
                        // STATS
                        setPokeHp(info.stats[0].base_stat);
                        setPokeAtk(info.stats[1].base_stat);
                        setPokeDef(info.stats[2].base_stat);
                        setPokeSpAtk(info.stats[3].base_stat);
                        setPokeSpDef(info.stats[4].base_stat);
                        setPokeSpeed(info.stats[5].base_stat);
                        // MOVES
                        setPokeMoves(info.moves.map(move => move.move.name));
            }}/>
        </main>
    );
} 

export default Busqueda;
